    {
        Prefix = "<prefix>",
    },
    AzureBlobFileListSource = new AzureBlobFileListContentSource(new Uri("http://localhost:3000"), "<fileList>"),
    Tags =
    {
        ["key"] = "<tags>"
    },
    MaxTrainingHours = 123.45F,
