Schema for Response Body:
{
  id: string,
  identity: {
    principalId: string,
    tenantId: string,
    type: "SystemAssigned"
  },
  location: string,
  name: string,
  properties: {
    cloudConnectors: {
      awsExternalId: string
    },
    createdAt: string (ISO 8601 Format),
    createdBy: string,
    createdByObjectId: string,
    endpoints: {
      catalog: string,
      guardian: string,
      scan: string
    },
    friendlyName: string,
    managedResourceGroupName: string,
    managedResources: {
      eventHubNamespace: string,
      resourceGroup: string,
      storageAccount: string
    },
    privateEndpointConnections: [
      {
        id: string,
        name: string,
        properties: {
          privateEndpoint: {
            id: string
          },
          privateLinkServiceConnectionState: {
            actionsRequired: string,
            description: string,
            status: "Unknown" | "Pending" | "Approved" | "Rejected" | "Disconnected"
          },
          provisioningState: string
        },
        type: string
      }
    ],
    provisioningState: "Unknown" | "Creating" | "Moving" | "Deleting" | "SoftDeleting" | "SoftDeleted" | "Failed" | "Succeeded" | "Canceled",
    publicNetworkAccess: "NotSpecified" | "Enabled" | "Disabled"
  },
  sku: {
    capacity: number,
    name: "Standard"
  },
  systemData: {
    createdAt: string (ISO 8601 Format),
    createdBy: string,
    createdByType: "User" | "Application" | "ManagedIdentity" | "Key",
    lastModifiedAt: string (ISO 8601 Format),
    lastModifiedBy: string,
    lastModifiedByType: "User" | "Application" | "ManagedIdentity" | "Key"
  },
  tags: Dictionary<string, string>,
  type: string
}

