Schema for EntityMutationResponse: { guidAssignments: Dictionary<string, string>, # Optional. A map of GUID assignments with entities. mutatedEntities: Dictionary<string, AtlasEntityHeader[]>, # Optional. The entity headers of mutated entities. partialUpdatedEntities: [ { classificationNames: [string], # Optional. An array of classification names. classifications: [ { entityGuid: string, # Optional. The GUID of the entity. entityStatus: "ACTIVE" | "DELETED", # Optional. Status of the entity - can be active or deleted. Deleted entities are not removed from Atlas store. removePropagationsOnEntityDelete: boolean, # Optional. Determines if propagations will be removed on entity deletion. validityPeriods: [ { endTime: string, # Optional. The end of the time boundary. startTime: string, # Optional. The start of the time boundary. timeZone: string, # Optional. The timezone of the time boundary. } ], # Optional. An array of time boundaries indicating validity periods. source: string, # Optional. indicate the source who create the classification detail sourceDetails: Dictionary<string, AnyObject>, # Optional. more detail on source information attributes: Dictionary<string, AnyObject>, # Optional. The attributes of the struct. typeName: string, # Optional. The name of the type. lastModifiedTS: string, # Optional. ETag for concurrency control. } ], # Optional. An array of classifications. displayText: string, # Optional. The display text. guid: string, # Optional. The GUID of the record. isIncomplete: boolean, # Optional. Whether it is a shell entity labels: [string], # Optional. labels meaningNames: [string], # Optional. An array of meanings. meanings: [ { confidence: number, # Optional. The confidence of the term assignment. createdBy: string, # Optional. The user who created the record. description: string, # Optional. The description of the term assignment. displayText: string, # Optional. The display text. expression: string, # Optional. The expression of the term assignment. relationGuid: string, # Optional. The GUID of the relationship. source: string, # Optional. The source of the term. status: "DISCOVERED" | "PROPOSED" | "IMPORTED" | "VALIDATED" | "DEPRECATED" | "OBSOLETE" | "OTHER", # Optional. The status of terms assignment. steward: string, # Optional. The steward of the term. termGuid: string, # Optional. The GUID of the term. } ], # Optional. An array of term assignment headers. status: "ACTIVE" | "DELETED", # Optional. Status of the entity - can be active or deleted. Deleted entities are not removed from Atlas store. attributes: Dictionary<string, AnyObject>, # Optional. The attributes of the struct. typeName: string, # Optional. The name of the type. lastModifiedTS: string, # Optional. ETag for concurrency control. } ], # Optional. An array of entity headers that partially updated. }

