Schema for Response Body: { guidAssignments: Dictionary<string, string>, mutatedEntities: Dictionary<string, AtlasEntityHeader[]>, partialUpdatedEntities: [ { attributes: Dictionary<string, AnyObject>, typeName: string, lastModifiedTS: string, classificationNames: [string], classifications: [ { attributes: Dictionary<string, AnyObject>, typeName: string, lastModifiedTS: string, entityGuid: string, entityStatus: "ACTIVE" | "DELETED", removePropagationsOnEntityDelete: boolean, validityPeriods: [ { endTime: string, startTime: string, timeZone: string } ], source: string, sourceDetails: Dictionary<string, AnyObject> } ], displayText: string, guid: string, isIncomplete: boolean, labels: [string], meaningNames: [string], meanings: [ { confidence: number, createdBy: string, description: string, displayText: string, expression: string, relationGuid: string, source: string, status: "DISCOVERED" | "PROPOSED" | "IMPORTED" | "VALIDATED" | "DEPRECATED" | "OBSOLETE" | "OTHER", steward: string, termGuid: string } ], status: "ACTIVE" | "DELETED" } ] }
Schema for Response Error: { requestId: string, errorCode: string, errorMessage: string }

