var credential = new DefaultAzureCredential();
var endpoint = new Uri("<https://my-service.azure.com>");
var client = new PurviewCatalogClient(endpoint, credential).GetPurviewTypesClient();

Response response = client.GetEnumDefByName("<name>");

JsonElement result = JsonDocument.Parse(response.ContentStream).RootElement;
Console.WriteLine(result.GetProperty("defaultValue").ToString());
Console.WriteLine(result.GetProperty("elementDefs")[0].GetProperty("description").ToString());
Console.WriteLine(result.GetProperty("elementDefs")[0].GetProperty("ordinal").ToString());
Console.WriteLine(result.GetProperty("elementDefs")[0].GetProperty("value").ToString());
Console.WriteLine(result.GetProperty("category").ToString());
Console.WriteLine(result.GetProperty("createTime").ToString());
Console.WriteLine(result.GetProperty("createdBy").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("availableLocales")[0].ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("calendar").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("lenient").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("numberFormat").GetProperty("availableLocales")[0].ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("numberFormat").GetProperty("currency").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("numberFormat").GetProperty("groupingUsed").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("numberFormat").GetProperty("maximumFractionDigits").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("numberFormat").GetProperty("maximumIntegerDigits").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("numberFormat").GetProperty("minimumFractionDigits").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("numberFormat").GetProperty("minimumIntegerDigits").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("numberFormat").GetProperty("parseIntegerOnly").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("numberFormat").GetProperty("roundingMode").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("timeZone").GetProperty("dstSavings").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("timeZone").GetProperty("id").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("timeZone").GetProperty("availableIds")[0].ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("timeZone").GetProperty("displayName").ToString());
Console.WriteLine(result.GetProperty("dateFormatter").GetProperty("timeZone").GetProperty("rawOffset").ToString());
Console.WriteLine(result.GetProperty("description").ToString());
Console.WriteLine(result.GetProperty("guid").ToString());
Console.WriteLine(result.GetProperty("name").ToString());
Console.WriteLine(result.GetProperty("options").GetProperty("<test>").ToString());
Console.WriteLine(result.GetProperty("serviceType").ToString());
Console.WriteLine(result.GetProperty("typeVersion").ToString());
Console.WriteLine(result.GetProperty("updateTime").ToString());
Console.WriteLine(result.GetProperty("updatedBy").ToString());
Console.WriteLine(result.GetProperty("version").ToString());
Console.WriteLine(result.GetProperty("lastModifiedTS").ToString());

Docs.remarks:
Below is the JSON schema for the response payload.
Response Body:
Schema for AtlasEnumDef: { defaultValue: string, # Optional. The default value. elementDefs: [ { description: string, # Optional. The description of the enum element definition. ordinal: number, # Optional. The ordinal of the enum element definition. value: string, # Optional. The value of the enum element definition. } ], # Optional. An array of enum element definitions. category: "PRIMITIVE" | "OBJECT_ID_TYPE" | "ENUM" | "STRUCT" | "CLASSIFICATION" | "ENTITY" | "ARRAY" | "MAP" | "RELATIONSHIP" | "TERM_TEMPLATE", # Optional. The enum of type category. createTime: number, # Optional. The created time of the record. createdBy: string, # Optional. The user who created the record. dateFormatter: { availableLocales: [string], # Optional. An array of available locales. calendar: number, # Optional. dateInstance: DateFormat, # Optional. The date format. dateTimeInstance: DateFormat, # Optional. The date format. instance: DateFormat, # Optional. The date format. lenient: boolean, # Optional. Determines the leniency of the date format. numberFormat: { availableLocales: [string], # Optional. The number format. currency: string, # Optional. The currency. currencyInstance: NumberFormat, # Optional. The number format. groupingUsed: boolean, # Optional. Determines if grouping is used. instance: NumberFormat, # Optional. The number format. integerInstance: NumberFormat, # Optional. The number format. maximumFractionDigits: number, # Optional. The maximum of fraction digits. maximumIntegerDigits: number, # Optional. The maximum of integer digits. minimumFractionDigits: number, # Optional. The minimum of fraction digits. minimumIntegerDigits: number, # Optional. The minimum of integer digits. numberInstance: NumberFormat, # Optional. The number format. parseIntegerOnly: boolean, # Optional. Determines if only integer is parsed. percentInstance: NumberFormat, # Optional. The number format. roundingMode: "UP" | "DOWN" | "CEILING" | "FLOOR" | "HALF_UP" | "HALF_DOWN" | "HALF_EVEN" | "UNNECESSARY", # Optional. The enum of rounding mode. }, # Optional. The number format. timeInstance: DateFormat, # Optional. The date format. timeZone: { dstSavings: number, # Optional. The value of the daylight saving time. id: string, # Optional. The ID of the timezone. availableIds: [string], # Optional. An array of available IDs. default: TimeZone, # Optional. The timezone information. displayName: string, # Optional. The display name of the timezone. rawOffset: number, # Optional. The raw offset of the timezone. }, # Optional. The timezone information. }, # Optional. The date format. description: string, # Optional. The description of the type definition. guid: string, # Optional. The GUID of the type definition. name: string, # Optional. The name of the type definition. options: Dictionary<string, string>, # Optional. The options for the type definition. serviceType: string, # Optional. The service type. typeVersion: string, # Optional. The version of the type. updateTime: number, # Optional. The update time of the record. updatedBy: string, # Optional. The user who updated the record. version: number, # Optional. The version of the record. lastModifiedTS: string, # Optional. ETag for concurrency control. }

