            }
        }
    }),
    Facets = {new SearchFacetItem
    {
        Count = 0,
        Facet = "assetType",
        Sort = new SearchFacetSort
        {
            Count = SearchSortOrder.Descend,
        },
    }, new SearchFacetItem
    {
        Count = 10,
