        hours: [number], # Optional.
        weekDays: ["Sunday" | "Monday" | "Tuesday" | "Wednesday" | "Thursday" | "Friday" | "Saturday"], # Optional.
        monthDays: [number], # Optional.
        monthlyOccurrences: [
          {
            additionalProperties: Dictionary<string, AnyObject>, # Optional. Dictionary of <AnyObject>
            day: "Sunday" | "Monday" | "Tuesday" | "Wednesday" | "Thursday" | "Friday" | "Saturday", # Optional.
            occurrence: number, # Optional.
          }
        ], # Optional.
      }, # Optional.
      timeZone: string, # Optional.
    }, # Optional.
    recurrenceInterval: string, # Optional.
    createdAt: string (ISO 8601 Format), # Optional.
    lastModifiedAt: string (ISO 8601 Format), # Optional.
    lastScheduled: string (ISO 8601 Format), # Optional.
    scanLevel: "Full" | "Incremental", # Optional.
    incrementalScanStartTime: string (ISO 8601 Format), # Optional.
