{
    deploymentId = "<deploymentId>",
    startDateTime = "2022-05-10T18:57:31.2311892Z",
    update = new
    {
        updateId = new
        {
            provider = "<provider>",
            name = "<name>",
            version = "<version>",
        },
    },
    groupId = "<groupId>",
});
Response response = client.CreateOrUpdateDeployment("<groupId>", "<deploymentId>", content);

