static ServiceClient GetServiceClient()
{
    try
    {
        var proxyHost = "localhost";
        var proxyPort = 8888;
        var proxy = new WebProxy(proxyHost, proxyPort);
        var transportSettings = new ServiceClientTransportSettings
        {
            AmqpProxy = proxy,
            HttpProxy = proxy
        };
